// Self-describing variable length encoding with reordering offset
sdvl_sn_lsb(field_width)
{
  UNCOMPRESSED {
    field [ field_width ];
  }

COMPRESSED lsb7 {
  discriminator =:= '0'   [ 1 ];
  field =:= msn_lsb(7)    [ 7 ];
}

COMPRESSED lsb14 {
  discriminator =:= '10'  [  2 ];
  field =:= msn_lsb(14)   [ 14 ];
}

COMPRESSED lsb21 {
  discriminator =:= '110'  [  3 ];
  field =:= msn_lsb(21)    [ 21 ];
}

COMPRESSED lsb28 {
  discriminator =:= '1110' [  4 ];
  field =:= msn_lsb(28)    [ 28 ];
}

  COMPRESSED lsb32 {
    discriminator =:= '11111111'        [  8 ];
    field =:= irregular(field_width)    [ field_width ];
  }
}

// Self-describing variable length encoding
sdvl_lsb(field_width)
