  field =:= lsb(21, ((2^21) / 4) - 1) [ 21 ];
}

COMPRESSED lsb28 {
  discriminator =:= '1110'            [  4 ];
  field =:= lsb(28, ((2^28) / 4) - 1) [ 28 ];
