ContentSet CONTENT-TYPE ::= {
    ct-ContentCollection | ct-ContentWithAttributes, ...
}

id-ct-contentCollection OBJECT IDENTIFIER ::= {
  iso(1) member-body(2) us(840) rsadsi(113549) pkcs(1) pkcs-9(9)
