contents.text:
partstatparam = element partstat {
    type-partstat-event |
    type-partstat-todo |
    type-partstat-jour
}

value-period = element period {
    element start {
        pattern-date-time
    },
    (
        element end {
            pattern-date-time
        } |
        element duration {
            pattern-duration
        }
    )
}

type-calprops = element properties {
    property-prodid &
    property-version &
    property-calscale? &
    property-method?
}

type-component = element components {
    (
        component-vevent |
        component-vtodo |
        component-vjournal |
        component-vfreebusy |
        component-vtimezone
    )*
}

component-vevent = element vevent {
    type-eventprop,
    element components {
        component-valarm+
    }?
}

component-vtodo = element vtodo {
    type-todoprop,
    element components {
        component-valarm+
    }?
}

component-vtimezone = element vtimezone {
    element properties {
        property-tzid &

        property-last-mod? &
        property-tzuurl?
    },
    element components {
        (component-standard | component-daylight) &
        component-standard* &
        component-daylight*
    }
}

component-valarm = element valarm {
    audioprop | dispprop | emailprop
}

    element latitude  { xsd:float },
    element longitude { xsd:float }
}
