  NOTIFICATIONS {
    g9983SvcUp,
    g9983SvcDown
  }
  STATUS      current

MODULE  -- this module
  MANDATORY-GROUPS {
    g9983BasicGroup,
    g9983AlarmConfGroup,
    g9983NotificationGroup
  }
