    bfdSessionGroup,
    bfdSessionReadOnlyGroup,
    bfdSessionPerfGroup,
    bfdNotificationGroup
}

bfdNotificationGroup NOTIFICATION-GROUP
    NOTIFICATIONS {
        bfdSessUp,
        bfdSessDown
    }
