}

uint32_t CRC32(const uint8_t* v, const int len) {
   const uint32_t poly = 0xedb88320UL;
   uint32_t crc, c;
   int i, k;
   crc = 0xffffffffUL;
   for (i = 0; i < len; ++i) {
      c = (crc ^ v[i]) & 0xff;
      for (k = 0; k < 8; k++) c = c & 1 ? poly ^ (c >> 1) : c >> 1;
      crc = c ^ (crc >> 8);
   }
   return crc ^ 0xffffffffUL;
}
