module ietf-datastores {
  yang-version 1.1;
  namespace "urn:ietf:params:xml:ns:yang:ietf-datastores";
  prefix ds;

This version of this YANG module is part of RFC 8342
(https://www.rfc-editor.org/info/rfc8342); see the RFC itself
for full legal notices.";

revision 2018-02-14 {
  description
    "Initial revision.";
  reference
    "RFC 8342: Network Management Datastore Architecture (NMDA)";
}

/*
 * Identities
 */

identity datastore {
  description
    "Abstract base identity for datastore identities.";
}

identity conventional {
  base datastore;
  description
    "Abstract base identity for conventional configuration
     datastores.";
}

identity running {
  base conventional;
  description
    "The running configuration datastore.";
}

identity candidate {
  base conventional;
  description
    "The candidate configuration datastore.";
}

identity startup {
  base conventional;
  description
    "The startup configuration datastore.";
}

identity intended {
  base conventional;
  description
    "The intended configuration datastore.";
}

identity dynamic {
  base datastore;
  description
    "Abstract base identity for dynamic configuration datastores.";
}

identity operational {
  base datastore;
  description
    "The operational state datastore.";
}

/*
 * Type definitions
 */

  typedef datastore-ref {
    type identityref {
      base datastore;
    }
    description
      "A datastore identity reference.";
  }
}

module ietf-origin {
  yang-version 1.1;
  namespace "urn:ietf:params:xml:ns:yang:ietf-origin";
  prefix or;

import ietf-yang-metadata {
  prefix md;
}

This version of this YANG module is part of RFC 8342
(https://www.rfc-editor.org/info/rfc8342); see the RFC itself
for full legal notices.";

revision 2018-02-14 {
  description
    "Initial revision.";
  reference
    "RFC 8342: Network Management Datastore Architecture (NMDA)";
}

/*
 * Identities
 */

identity origin {
  description
    "Abstract base identity for the origin annotation.";
}

identity intended {
  base origin;
  description
    "Denotes configuration from the intended configuration
     datastore.";
}

identity dynamic {
  base origin;
  description
    "Denotes configuration from a dynamic configuration
     datastore.";
}

identity system {
  base origin;
  description
    "Denotes configuration originated by the system itself.

     Examples of system configuration include applied configuration
     for an always-existing loopback interface, or interface
     configuration that is auto-created due to the hardware
     currently present in the device.";
}

     Examples of protocols that provide learned configuration
     include link-layer negotiations, routing protocols, and
     DHCP.";
}

identity default {
  base origin;
  description
    "Denotes configuration that does not have a configured or
     learned value but has a default value in use.  Covers both
     values defined in a 'default' statement and values defined
     via an explanation in a 'description' statement.";
}

identity unknown {
  base origin;
  description
    "Denotes configuration for which the system cannot identify the
     origin.";
}

/*
 * Type definitions
 */

typedef origin-ref {
  type identityref {
    base origin;
  }
  description
    "An origin identity reference.";
}

/*
 * Metadata annotations
 */

  md:annotation origin {
    type origin-ref;
    description
      "The 'origin' annotation can be present on any configuration
       data node in the operational state datastore.  It specifies
       from where the node originated.  If not specified for a given
       configuration data node, then the origin is the same as the
       origin of its parent node in the data tree.  The origin for
       any top-level configuration data nodes must be specified.";
  }
}

module example-ds-ephemeral {
  yang-version 1.1;
  namespace "urn:example:ds-ephemeral";
  prefix eph;

import ietf-datastores {
  prefix ds;
}
import ietf-origin {
  prefix or;
}

// datastore identity
identity ds-ephemeral {
  base ds:dynamic;
  description
    "The ephemeral dynamic configuration datastore.";
}

  // origin identity
  identity or-ephemeral {
    base or:dynamic;
    description
      "Denotes data from the ephemeral dynamic configuration
       datastore.";
  }
}

module example-system {
  yang-version 1.1;
  namespace urn:example:system;
  prefix sys;

import ietf-inet-types {
  prefix inet;
}

container system {
  leaf hostname {
    type string;
  }

leaf name {
  type string;
}

container auto-negotiation {
  leaf enabled {
    type boolean;
    default true;
  }
  leaf speed {
    type uint32;
    units mbps;
    description
      "The advertised speed, in Mbps.";
  }
}

leaf speed {
  type uint32;
  units mbps;
  config false;
  description
    "The speed of the interface, in Mbps.";
}

        leaf ip {
          type inet:ip-address;
        }
        leaf prefix-length {
          type uint8;
        }
      }
    }
  }
}

container bgp {
  leaf local-as {
    type uint32;
  }
  leaf peer-as {
    type uint32;
  }
  list peer {
    key name;
    leaf name {
      type inet:ip-address;
    }
    leaf local-as {
      type uint32;
      description
        "... Defaults to ../local-as.";
    }
    leaf peer-as {
      type uint32;
      description
        "... Defaults to ../peer-as.";
    }
    leaf local-port {
      type inet:port;
    }
    leaf remote-port {
      type inet:port;
      default 179;
    }
    leaf state {
      config false;
      type enumeration {
        enum init;
        enum established;
        enum closing;
      }
    }
  }
}

container interfaces {
  list interface {
    key name;
    leaf name {
      type string;
    }
    leaf description {
      type string;
    }
    leaf mtu {
      type uint16;
    }
    leaf-list ip-address {
      type inet:ip-address;
    }
  }
}
