module ietf-foo {
  namespace "urn:ietf:params:xml:ns:yang:ietf-foo";
  prefix "foo";
  organization "...";
  contact "...";
  description "...";
  revision 2016-03-20 {
    description "Latest revision";
    reference "RFC XXXX: Foo Protocol";
  }
  // ... more statements
}

module example-foo {
  namespace "tag:example.com,2017:example-foo";
  prefix f;

module example-bar {
  namespace "tag:example.com,2017:example-bar";
  prefix b;

module example-one {
  namespace "tag:example.com,2017:example-one";
  prefix one;
  import example-foo { prefix f; }
  import example-bar { prefix b; }

  augment "/f:foo" {
     leaf foo { type b:foo; }
  }
}

notification foo {
  leaf mtu {
    // NOT okay because when-stmt context is this notification
    when "/if:interfaces/if:interface[name='eth0']";
    type leafref {
      // Okay because path-stmt has a different context
      path "/if:interfaces/if:interface/if:mtu";
    }
  }
}

augment "/rt:active-route/rt:input/rt:destination-address" {
   when "rt:address-family='v4ur:ipv4-unicast'" {
     description
       "This augment is valid only for IPv4 unicast.";
   }
   // nodes defined here within the augment-stmt
   // cannot be referenced in the when-stmt
}

when /foo/services/*/active {
  description
    "No services directly defined in this module.
     Matches objects that have augmented the services container.";
}

leaf foo1 {
  type enumeration {
     enum one;
     enum two;
     enum three;
  }
}

leaf foo2 {
  // INCORRECT
  must "/f:foo1 != 'two'";
  type string;
}

leaf foo2 {
  // CORRECT
  must "/f:foo1 = 'one' or /f:foo1 = 'three'";
  type string;
}

leaf foo1 {
  type enumeration {
     enum one;
     enum two;
     enum three;
     enum four;
  }
}

import ietf-yang-types {
   prefix yang;
   reference "RFC 6991: Common YANG Data Types";
}

leaf reserved {
  type string;
  description
    "This object has no purpose at this time, but a future
     revision of this module might define a purpose
     for this object.";
  }
}

revision "2012-02-22" {
  description
    "Initial version";
  reference
    "RFC 8341: Network Configuration
               Access Control Model";
}

revision "2017-12-11" {
  description
    "Added support for YANG 1.1 actions and notifications tied to
     data nodes.  Clarify how NACM extensions can be used by other
     data models.";
  reference
    "RFC 8407: Network Configuration Protocol (NETCONF)
               Access Control Model";
}

revision "2012-02-22" {
  description
    "Initial version";
  reference
    "RFC 8341: Network Configuration
               Access Control Model";
}

leaf foo {
  type enumeration {
    enum one;
    enum two;
  }
}

identity foo-type {
  description "Base for the extensible type";
}

identity one {
  base f:foo-type;
}
identity two {
  base f:foo-type;
}

leaf foo {
  type identityref {
    base f:foo-type;
  }
}

typedef ipv4-address-no-zone {
  type inet:ipv4-address {
    pattern '[0-9\.]*';
  }
  ...
}

typedef yang-identifier {
  type string {
    length "1..max";
    pattern '[a-zA-Z_][a-zA-Z0-9\-_.]*';
    pattern '.|..|[^xX].*|.[^mM].*|..[^lL].*';
  }
  ...
}

typedef dscp {
  type uint8 {
     range "0..63";
  }
  ...
}

leaf foo {
  // INCORRECT
  type enumeration {
    enum one;
    enum two;
  }
  description
    "The foo enum...
     one: The first enum
     two: The second enum";
}

leaf foo {
  // CORRECT
  type enumeration {
    enum one {
      description "The first enum";
    }
    enum two {
      description "The second enum";
    }
  }
  description
    "The foo enum...  ";
}

type union {
  type string;
  type enumeration {
    enum up;
    enum down;
  }
}

type union {
  type enumeration {
    enum up;
    enum down;
  }
  type string;
}

type union {
  type string;
  type int32;
}

type union {
  type int32;
  type string;
}

leaf flag1 {
  type empty;
}

leaf flag2 {
  type boolean;
  default false;
}

container top {
   container foos {
      list foo { ... }
   }
   container bars {
      list bar { ... }
   }
}

container top {
   list foo { ... }
   list bar { ... }
}

notification interface-up {
  description "Sent when an interface is activated.";
  leaf name {
    type leafref {
      path "/if:interfaces/if:interface/if:name";
    }
  }
}

feature feature1 {
  description "Some protocol feature";
}

feature feature2 {
  if-feature "feature1";
  description "Another protocol feature";
}

    container ethernet {
        leaf duplex {
            ...
        }
    }
}

yang-version 1.1;
namespace "tag:example.com,2017:example-module";
prefix mymod;

identity some-new-iftype {
   base iana:iana-interface-type;
}

     leaf mandatory-leaf {
        type string;
        mandatory true;
     }
  }
}

container backups {
  list backup {
     ...
     max-elements  10;
     ...
  }
}

deviation /bk:backups/bk:backup {
  deviate add {
     max-elements  10;
  }
}

list foo {
   key name;
   leaf name {
     type string;
   }
   ...
}

list foo-addon {
   key name;
   config false;
   leaf name {
     type string;
   }
   ...
}

list foo {
   key name;
   leaf name {
     type string;
   }
   ...
}

list foo-addon {
   key name;
   config false;
   leaf name {
     type leafref {
       path "/foo/name";
       require-instance false;
     }
   }
   leaf addon {

     type string;
     mandatory true;
   }
}

container foo {
  ...
  // contains "config true" and "config false" nodes that have
  // no corresponding "config true" object (e.g., counters)
}

module example-foo {
  namespace "urn:example.com:params:xml:ns:yang:example-foo";
  prefix "foo";

   container foo {
     // configuration data child nodes
     // operational value in operational state datastore only
     // may contain "config false" nodes as needed
   }
}

module example-foo {
  namespace "urn:example.com:params:xml:ns:yang:example-foo";
  prefix "foo";

container foo {
  // configuration data child nodes
}

   container foo-state {
     config false;
     // operational state child nodes
   }
}

module example-foo {
  namespace "urn:example.com:params:xml:ns:yang:example-foo";
  prefix "foo";

container foo {
  // configuration data child nodes
  // operational value in operational state datastore only
  // may contain "config false" nodes as needed
  // will contain any data nodes from old foo-state
}

   // keep original foo-state but change status to deprecated
   container foo-state {
     config false;
     status deprecated;
     // operational state child nodes
   }
}

module example-foo-state {
  namespace "urn:example.com:params:xml:ns:yang:example-foo-state";
  prefix "foo-s";

   container foo-state {
     config false;
     status deprecated;
     // operational state child nodes
   }
}

rpc reset {
  input {
    leaf interface {
      type if:interface-ref;
      mandatory true;
      description "The interface to reset.";
    }
  }
}

container interfaces {
  list interface {
    ...
    action reset { }
  }
}

